function TilesMap.isSolid(pID)
  --print(pID)
  local bool
  if pID == 0 then bool = true end
  if pID == 1 then bool = true end
  if pID == 3 then print("je suis là")bool = false end
  if pID == 4 then bool = false end
  if pID == 5 then bool = false end
  if pID == 7 then bool = false end
  if pID == 8 then bool = false end
  if pID == 9 then bool = false end
  if pID == 10 then bool = false end
  if pID == 11 then bool = false end
  if pID == 12 then bool = true end
  if pID == 13 then  bool = true end
  if pID == 999 then bool = false end

  return bool
end


TilesMap.Draw = function()
  local c,l
  for l=1,TilesMap.MAP_HEIGHT do
    for c=1,TilesMap.MAP_WITDH do
      local id = TilesMap.Map[l][c]
      
      if TilesMap.TileTextures[id] ~= nil then
        love.graphics.draw(TilesMap.TileTextures[id], ((c-1)*TilesMap.TILE_WIDTH) - TilesMap.positionOriginActuelX, (l-1)*TilesMap.TILE_HEIGHT - TilesMap.positionOriginActuelY)
      end
    end
  
  end
end

return TilesMap